function ProcessTrackerMessage (name, line, wildcards, styles) --This part of the function sends the line to Fiendish's Chat Window local styles_to_colors = StylesToColoursOneLine(styles) --CallPlugin("b555825a4a5700c35fa80780","storeFromOutside",styles_to_colors) CallPlugin("b555825a4a5700c35fa80780", "storeFromOutside", "[" .. os.date("%b %d, %H:%M:%S") .. "] ".. StylesToColours(styles), tab)
--This stuff is for the tracker channel history function if (#tracker_history >= 100) then -- Only keep last 100 so add and remove. tracker_history = table.remove(tracker_history, 1) table.insert(tracker_history, styles) else table.insert(tracker_history, styles) end end -- a
function Print_Tracker_History() for key, value in pairs(tracker_history) do styles = value for _, v in ipairs (styles) do --Borrowed this code from here: http://www.gammon.com.au/forum/bbshowpost.php?bbsubject_id=9713 --Why must printing the same line with coloring be so complicated. ColourTell (RGBColourToName (v.textcolour), RGBColourToName (v.backcolour), v.text) end -- for each style run Note("") -- needed to make a new line end end
